import React from 'react'

function Product({post}) {
  let selected=false;
  return (
    <div className='flex flex-col items-center justify-between rounded-xl mt-10 ml-5 gap-3 p-4'>
      <div className='truncate w-40 mt-1 text-gray-700 font-semibold text-lg  text-left '>
        {post.title}
      </div>
      <div className=' w-40 text-gray-400 font-normal text-[10px] text-left'>
        {post.description}
      </div>
      <div className='h-[180px]'>
        <img src={post.image}/>
      </div>
      <div className='flex flex-row'>
        <p>{post.price}</p>
        <button>
          {
            selected ? <p>Remove Item</p> : <p> Add to Cart</p>
          }
        </button>
      </div>
    </div>
  )
}

export default Product
