import React from 'react'

function Product({post}) {
  return (
    <div>
      <div>
        {post.title}
      </div>
      <div>
        {post.description}
      </div>
      <div>
        <img src={post.image}/>
      </div>
      <div>
        <p>{post.price}</p>
        <button>
          {
            selected ? <p>Remove Item</p> : <p> Add to Cart</p>
          }
        </button>
      </div>
    </div>
  )
}

export default Product
